  margin: 0 auto;

  background-color: white;
  color: black;
  font: 400 15px/1.467 'Open Sans';
  letter-spacing: 0.03em;

  text-align: left;
  overflow: hidden;
  overflow-wrap: break-word;
  word-break: break-word;
}

.card-boundary .embedded-card {
  margin: inherit;
  padding: inherit;
  background-color: inherit;
  color: inherit;
  font: inherit;
  letter-spacing: inherit;
}

